(defn marginalize [tensor state]
  (reduce + (flatten (get-in tensor state))))
;; @@
;; =>
;;; {"type":"html","content":"<span class='clj-var'>#&#x27;less-extreme-anti-bias-driven/marginalize</span>","value":"#'less-extreme-anti-bias-driven/marginalize"}
;; <=

;; @@
(def joint-tensor
  (a/get-hdf5-tensor store (:dist_joint_sim.h5 blobs) "/dist_joint_sim"))
;; @@
;; =>
;;; {"type":"html","content":"<span class='clj-var'>#&#x27;less-extreme-anti-bias-driven/joint-tensor</span>","value":"#'less-extreme-anti-bias-driven/joint-tensor"}
;; <=

;; @@
(marginalize joint-tensor [1 1])
;; @@

;; @@
(plot/compose 
  #_(plot/bar-chart states (map (partial marginalize joint-tensor) states)
                  :color "red" :opacity 0.5 :plot-size 600) 
  (plot/bar-chart states (map (a/sample-freqs samples) states) 
                  :opacity 0.5))
