(def trad-theo (reduce (fn [f s] (update-in f [(take 2 s)] 
                             (fnil + 0)
                             (f/prob (full-matrix (:restricted-weights trad-rbm)) 
                           (concat (:v-biases trad-rbm) (:h-biases trad-rbm)) s)))
        {}
     (f/state-space 8)))
(map trad-theo states)
